import{graphql}from"gatsby";export const globalFragments=graphql`
  fragment NavFragment on Query {
    siteConfig {
      logo
      nav {
        type
        label
        link
        external
        activateWithSidebar
        target
        icon
        items {
          type
          label
          link
          external
          activateWithSidebar
          target
          icon
          items {
            type
            label
            link
            icon
            external
            activateWithSidebar
            target
          }
        }
      }
    }
  }

  fragment NavItemDetails on NavItem {
    label
    link
    httpVerb
    type
    expanded
    selectFirstItemOnExpand
    pageId
    redocPrevLink
    redocPrevLabel
    redocNextLink
    redocNextLabel
    redocFakePage
    external
    target
    menuStyle
    sublabel
    icon
    separatorLine
    apiVersionId
    isDefaultApiVersion
  }

  fragment DeepNavItems on NavItem {
    ...NavItemDetails
    items {
      ...NavItemDetails
      items {
        ...NavItemDetails
        items {
          ...NavItemDetails
          items {
            ...NavItemDetails
            items {
              ...NavItemDetails
              items {
                ...NavItemDetails
                items {
                  ...NavItemDetails
                }
              }
            }
          }
        }
      }
    }
  }

  fragment FooterFragment on Query {
    siteConfig {
      footer {
        copyrightText
        links {
          ...NavItemDetails
        }
        columns {
          label
          group
          items {
            ...NavItemDetails
          }
        }
      }
      siteVersion
    }
  }
`;